import axios from 'axios'
import { withRouter,Link} from 'react-router-dom';
import main from '../images/main.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faBars} from '@fortawesome/free-solid-svg-icons';
import { useSelector } from 'react-redux';
import { useEffect, useState } from 'react';


function Navmenu(props) {
  const isLogged = useSelector(state=>state.user.isLoggedIn);
  const [scroll,setScroll] = useState(0);

  const handleFollow = () => {
    setScroll(window.pageYOffset); // window 스크롤 값을 ScrollY에 저장
  }

  useEffect(() => {
    const watch = () => {
        window.addEventListener('scroll', handleFollow);
      }
    console.log("ScrollY is ", scroll); // ScrollY가 변화할때마다 값을 콘솔에 출력
    watch(); // addEventListener 함수를 실행
    return () => {
      window.removeEventListener('scroll', handleFollow); // addEventListener 함수를 삭제
    }
  }, [scroll])


  const onLogoutHandler = () => {
    axios.get('/api/user/logout')
    .then(response => {
      if(response.data.success) {
        alert("로그아웃되었습니다.")
        window.location.replace("/")
      } else {
        alert("로그아웃 실패")
      }
    })
    }
    return(
      <div className='headerNavContainer'>
        <header className={scroll=== 0 ? "headerNav" : "headerNav scrolled"}>
        <FontAwesomeIcon icon={faBars} className={scroll === 0 ? "faBar" : "faBar scrolled"}></FontAwesomeIcon>
        <div className='logo'>
          <Link to="/" className='linkNone'>
            <div className={scroll === 0 ? "logoTitle" : "logoTitle scrolled"}>Cosme</div>
          </Link>
        </div>
          <div className='headerNavMenu'>
            {isLogged == true ? <div onClick={onLogoutHandler} className='linkNone navClick'>로그아웃</div> : null}
            {isLogged == true ? <a href="/upload" className='linkNone navClick'>상품등록</a> : null}
          </div>
          <a href="/api/cart"><FontAwesomeIcon icon={faCartShopping} className="cartIcon"></FontAwesomeIcon></a>
        </header>
      </div>
    )
  }

  export default withRouter(Navmenu);
