import React, {useEffect,useState,useRef} from 'react'
import axios from 'axios'
import main from "../images/main.jpg";
import main2 from "../images/main2.jpg";
import main3 from "../images/main3.jpg";
import {withRouter} from "react-router-dom";
import { FontAwesomeIcon,faBars } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';
import Search from './search';
import styled, {css} from 'styled-components'



function Landing() {
    const TOTAL_SLIDES = 2;
    const [Skip, setSkip] = useState(0) //데이터 시작할 부분
    const [Limit, setLimit] = useState(6) // 몇개의 데이터를 가져올지
    const [Total,setTotal] =useState(0);
    const [Image,setImage] = useState([main,main2,main3]);
    const [product,productState] = useState([]);
    const [searchTerm,setSearchTerm] = useState("");

    useEffect(()=>{
        const variables = {
            skip: Skip,
            limit: Limit,
        }

        getProducts(variables)
    },[])

    const loadMoreHandler = (e) => {
        let skip = Skip + Limit;

        
        const variables = {
            skip: skip,
            limit: Limit,
            loadMore: true
            
        }
        getProducts(variables)
        setSkip(skip)

    }

    const updateSearchTerm = (newSearch) => {
        let body = {
            skip: 0,
            Limit: Limit,
            searchTerm: newSearch
        }

        setSkip(0);
        setSearchTerm(newSearch);
        getProducts(body)
    }

    const getProducts = (variables) => {
       
        axios.post("/api/product/get", variables)
        .then(response => {
            if(response.data.success) {
                if(variables.loadMore) {
                    //더보기버튼 작동시 기존 데이터 + 새 데이터 setstate
                    productState([...product,...response.data.productInfo])
                } else {
                    productState(response.data.productInfo)
                    setTotal(response.data.total); 
                }
            } else {
                alert("상품리스트 조회 실패");
            }
        })
    }

   

    const productList = product.map((product, index) => {
        return (
            <div key={product._id} className='proListContainer'>
                <a href={`/api/product/${product._id}`}><img src={`http://localhost:5000/${product.image}`} alt="productImage" className='proListImage'/></a>
                <div className="productDesc"key={product.index}>{product.title}</div>
                <div className="productDesc" key={product.title}>{product.price}</div>
            </div>
        )
        
    })
  

    return (
        <div className='landing'>
            <div className='headerNavContainer'>
        <header className="headerNav">
        <FontAwesomeIcon icon={faBars} className="faBar"></FontAwesomeIcon>
        <div className='logo'>
          <Link to="/" className='linkNone'>
            <div className='logoTitle'>Cosme</div>
          </Link>
        </div>
          <div className='headerNavMenu'>
            {isLogged == true ? <div onClick={onLogoutHandler} className='linkNone navClick'>로그아웃</div> : null}
            {isLogged == true ? <a href="/upload" className='linkNone navClick'>상품등록</a> : null}
          </div>
          <a href="/api/cart"><FontAwesomeIcon icon={faCartShopping} className="cartIcon"></FontAwesomeIcon></a>
        </header>
      </div>
            <div className='productContainer'>
                <img src={main} alt="mainImage" className='mainImage'/>
                <div className='bestSeller'>Best seller</div>
                <Search getProducts={getProducts} refreshFunction={updateSearchTerm}></Search>
                <div className='proList'>
                    {productList}
                </div>
                { Total > product.length &&
                    <div className='readMore'><button className='readMoreBtn' onClick={loadMoreHandler}>+ 더보기</button></div> }
            </div>
        </div>
    )
}

const Container = styled.div`
    width:100%
    height: 100%;
`;

const Button = styled.button`
  all: unset;
  border: 1px solid coral;
  padding: 0.5em 2em;
  color: coral;
  border-radius: 10px;
  &:hover {
    transition: all 0.3s ease-in-out;
    background-color: coral;
    color: #fff;
  }
`;
const SliderContainer = styled.div`
    height: 100%;
    display: flex;
    flex-direction:column;

`;


export default withRouter(Landing);
