import React, {useEffect,useState,useRef} from 'react'
import axios from 'axios'
import main from "../images/main.jpg";
import main2 from "../images/main2.jpg";
import main3 from "../images/main3.jpg";
import Slide from "./slide.js";
import {withRouter} from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import Search from './search';
import styled, {css} from 'styled-components'



function Landing() {
    const TOTAL_SLIDES = 2;
    const [Skip, setSkip] = useState(0) //데이터 시작할 부분
    const [Limit, setLimit] = useState(6) // 몇개의 데이터를 가져올지
    const [Total,setTotal] =useState(0);
    const [Image,setImage] = useState([main,main2,main3]);
    const [product,productState] = useState([]);
    const [searchTerm,setSearchTerm] = useState("");
    const [currentSlide, setCurrentSlide] = useState(0);
    const slideRef = useRef(null);
    
    useEffect(() => {
        slideRef.current.style.transition = "all 0.5s ease-in-out";
        slideRef.current.style.transform = `translateX(-${currentSlide}00%)`; // 백틱을 사용하여 슬라이드로 이동하는 애니메이션을 만듭니다.
      }, [currentSlide]);

    useEffect(()=>{
        const variables = {
            skip: Skip,
            limit: Limit,
        }

        getProducts(variables)
    },[])

    const loadMoreHandler = (e) => {
        let skip = Skip + Limit;

        
        const variables = {
            skip: skip,
            limit: Limit,
            loadMore: true
            
        }
        getProducts(variables)
        setSkip(skip)

    }

    const updateSearchTerm = (newSearch) => {
        let body = {
            skip: 0,
            Limit: Limit,
            searchTerm: newSearch
        }

        setSkip(0);
        setSearchTerm(newSearch);
        getProducts(body)
    }

    const getProducts = (variables) => {
       
        axios.post("/api/product/get", variables)
        .then(response => {
            if(response.data.success) {
                if(variables.loadMore) {
                    //더보기버튼 작동시 기존 데이터 + 새 데이터 setstate
                    productState([...product,...response.data.productInfo])
                } else {
                    productState(response.data.productInfo)
                    setTotal(response.data.total); 
                }
            } else {
                alert("상품리스트 조회 실패");
            }
        })
    }

   

    const productList = product.map((product, index) => {
        return (
            <div key={product._id} className='proListContainer'>
                <a href={`/api/product/${product._id}`}><img src={`http://localhost:5000/${product.image}`} alt="productImage" className='proListImage'/></a>
                <div className="productDesc"key={product.index}>{product.title}</div>
                <div className="productDesc" key={product.title}>{product.price}</div>
            </div>
        )
        
    })
  
    const nextSlide = () => {
        if (currentSlide >= TOTAL_SLIDES) { // 더 이상 넘어갈 슬라이드가 없으면 슬라이드를 초기화합니다.
          setCurrentSlide(0);
        } else {
          setCurrentSlide(currentSlide + 1);
        }
      };
      const prevSlide = () => {
        if (currentSlide === 0) {
          setCurrentSlide(TOTAL_SLIDES);
        } else {
          setCurrentSlide(currentSlide - 1);
        }
      };
    return (
        <div className='landing'>
            <Container>
            <SliderContainer ref={slideRef}>
                <Slide img={main} />
                <Slide img={main2} />
                <Slide img={main3} />
            </SliderContainer>
            <prevButton onClick={prevSlide}><FontAwesomeIcon icon={faArrowLeft}></FontAwesomeIcon></prevButton>
            <nextButton onClick={nextSlide}><FontAwesomeIcon icon={faArrowRight}></FontAwesomeIcon></nextButton>
            </Container>
            <div className='productContainer'>
                <div className='bestSeller'>Best seller</div>
                <Search getProducts={getProducts} refreshFunction={updateSearchTerm}></Search>
                <div className='proList'>
                    {productList}
                </div>
                { Total > product.length &&
                    <div className='readMore'><button className='readMoreBtn' onClick={loadMoreHandler}>+ 더보기</button></div> }
            </div>
        </div>
    )
}

const Container = styled.div`
  width: 30%;
  overflow-x: hidden;
`;

const prevButton = styled.button`
    position:absolute;
    bottom:1000px;
    all: unset;
    border: 1px solid coral;
    padding: 0.5em 2em;
    color: coral;
    border-radius: 10px;
  }
`;
const nextButton = styled.button`
position:absolute;
all: unset;
border: 1px solid coral;
padding: 0.5em 2em;
color: coral;
border-radius: 10px;
}
`;
const SliderContainer = styled.div`
    width:100%;
    display: flex;
    overflow-wrap:anywhere;

`;


export default withRouter(Landing);
