import {UPLOAD_PRODUCT} from "../_actions/types";

export const product = function(state = {}, action) {
    switch(action.type) {
        case UPLOAD_PRODUCT:
            return {...state, productData: action.payload}
            break;
        default:
            return state;
    }
}
