import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';

function Cart(props) {
    const [Product,setProduct] = useState([]);
    const [Cart,setCart] = useState([]);

    useEffect(()=>{
        axios.post("api/product/mycart")
        .then(response => {
            if(response.data.success) {
                setProduct(response.data.productInfo)
                setCart(response.data.userInfo.cart)
            } else {
                console.log("상품정보 로드 실패")
            }
    })},[])
    return (
        <div>
            <div>내 카트</div>
            <div>
                <div>
                    <div>이미지</div>
                    <div>상품명</div>
                    <div>수량</div>
                    <div>옵션</div>
                    <div>주문일시</div>
                </div>
                <div>
                   { Product.map((item) => {
                       <div>
                      <div>{item.image}</div> 
                      <div>{item.title}</div>
                      </div>
                   })}
                </div>
            </div>
        </div>
    )
}


export default withRouter(Cart);
