import axios from 'axios';
import React, { Component, useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';

function Detail (props) {
    const [Product,setProduct] = useState([]);
    const productId = props.match.params.productId

    useEffect(()=> {
    axios.post(`/api/product/products_by_id?id=${productId}&type=single`)
    .then(response => {
        if(response.data.success) {
            setProduct(response.data.productInfo[0])
            
        } else {
            alert("상품불러오기 실패")
        }
    })},[])
    
    return (
        <section className='detailContainer'>
            <img className='detailImage' src={`http://localhost:5000/${Product.image}`}/>
            <div className='detailTitle'>{Product.title}
            <br/><span className='detailPrice'>{Product.price}</span>
            <br/><select name="detailSelect">
                <option value="none">옵션선택</option>
                <option value={Product.option}>{Product.option}</option>
            </select>
            <br/><button>장바구니</button>
            </div>
            
        </section>
    )
}

  export default withRouter(Detail);
