import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';

function App() {
  let [title,titleState] = useState(['상품1', '상품2', '상품3']);
  let [detail,detailState] = useState(['설명1', '설명2' , '설명3']);

  let [modal,modalState] = useState(false);

  function changeTitle() {
    //state는 등호기호로 직접 조작할 수 없다.
    var newTitle = [...title];
    newTitle[0] = '새 상품1';
    titleState( newTitle );
  }
  return (
    <div className="App">
      <div className="shop-nav">
        <div>shop</div>
      </div>
    {
     modal === true ?
     <Modal />
     : null 
    }

    {
      title.map(function(a) {
        return <div className="shop-list">
        <div onClick={ () => {modalState(true)}}>{ a } </div>
        <button onClick= {changeTitle}>상품수정</button>
        <hr/>
        </div>
      })

    }
    </div>
  );
}

function Modal () {
  return (
       <div className='modal'>
        <h2>상세 제목</h2>
        <div>상세 날짜</div>
        <div>상세 내용</div>
      </div>
  )
}
export default App;
