import axios from 'axios'
import { withRouter,Link} from 'react-router-dom';
import main from '../images/main.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faUser, faAddressCard} from '@fortawesome/free-solid-svg-icons';
import { useSelector } from 'react-redux';
import { useEffect, useState } from 'react';


function Navmenu(props) {
  const isLogged = useSelector(state=>state.user.isLoggedIn);



  const onLogoutHandler = () => {
    axios.get('/api/user/logout')
    .then(response => {
      if(response.data.success) {
        alert("로그아웃되었습니다.")
        window.location.replace("/")
      } else {
        alert("로그아웃 실패")
      }
    })
    }
    return(
      <div className='headerNavContainer'>
        <header className="headerNav">
            <div className='logo'>
              <Link to="/" className='linkNone'>
                <div className='logoTitle'>Cosme</div>
              </Link>
            </div>
            <div className='headerNavMenu'>
              {isLogged == false ? <a href="/user/login" className='linkNone navClick'><FontAwesomeIcon icon={faUser}></FontAwesomeIcon></a> : null}
              {isLogged == false ? <a href="/user/signup" className='linkNone navClick'><FontAwesomeIcon icon={faAddressCard}></FontAwesomeIcon></a> : null}
              {isLogged == true ? <div onClick={onLogoutHandler} className='linkNone navClick'>로그아웃</div> : null}
              {isLogged == true ? <a href="/upload" className='linkNone navClick'>상품등록</a> : null}
            </div>
            {isLogged == true && <a href="/api/cart"><FontAwesomeIcon icon={faCartShopping} className="cartIcon"></FontAwesomeIcon></a>}
        </header>
      </div>
    )
  }

  export default withRouter(Navmenu);
